	        ,ITEM_MADE_DATE


	        ,ITEM_EXPIRY_DATE


	        ,ITEM_ORIGINAL_FILE, ITEM_SAVED_FILE

	    )
	    VALUES (
	        LIVING_GOODS_SEQ.NEXTVAL,
	        #{itemName},
	        #{itemCategory},
	        #{itemQuantity}

	        ,#{itemPrice}


	        ,#{itemInputDate}


	        ,#{itemPurchaseDate}


	        ,#{itemMadeDate}


	        ,#{itemExpiryDate}


	        ,#{itemOriginalFile}, #{itemSavedFile}

	    )
